import React, { useState } from "react";

function Contact() {
  const [result, setResult] = useState("");

  const onSubmit = async (event) => {
    event.preventDefault();
    setResult("Sending....");
    const formData = new FormData(event.target);

    formData.append("access_key", "8106ecce-1b1a-43c3-94dc-bd4e4139cb7b");

    const response = await fetch("https://api.web3forms.com/submit", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();

    if (data.success) {
      setResult("Form Submitted Successfully");
      event.target.reset();
    } else {
      console.log("Error", data);
      setResult(data.message);
    }
  };

  return (
    <div className="max-w-lg mx-auto p-6 bg-zinc-700 rounded-lg shadow-2xl font-mono">
      <form onSubmit={onSubmit} className="flex flex-col gap-4">
        <input
          type="text"
          name="name"
          required
          className="bg-transparent  text-white border border-gray-300 p-3 rounded-md focus:outline-none focus:border-green-500"
          placeholder="name"
        />

        <input
          type="email"
          name="email"
          required
          className="bg-transparent text-white border border-gray-300 p-3 rounded-md focus:outline-none focus:border-green-500"
          placeholder="email"
        />

        <textarea
          name="message"
          required
          className="bg-transparent  text-white border border-gray-300 p-3 rounded-md focus:outline-none focus:border-green-500 resize-y"
          rows="4"
          placeholder="message"
        ></textarea>

        <button
          type="submit"
          className="bg-green-500 text-white py-3 rounded-md hover:bg-green-600 transition duration-300"
        >
          Contact Me / Hire Me
        </button>
      </form>

      <span className="block text-center mt-6 text-lg text-white font-semibold">
        {result}
      </span>
    </div>
  );
}

